import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const { user } = useAuth(); // Changed from currentUser to user (based on your AuthContext)

  const handleSettings = () => {
    navigate('/profile');
  };

  const handleExplore = () => {
    navigate('/countries');
  };

  const handleMyTrips = () => {
    navigate('/my-bookings'); // Or wherever your trips are listed
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Add search functionality if needed
    alert('Search functionality to be implemented');
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Travel Itinerary Planner</h1>
        <div className="header-actions">
          <span className="welcome-text">Welcome, {user?.name || 'Traveler'}!</span>
          <button className="settings-btn" onClick={handleSettings} title="Profile Settings">
            ⚙️
          </button>
        </div>
      </header>
      
      <div className="hero-section">
        <img 
          src="https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=1200" 
          alt="Travel" 
          className="hero-image"
        />
        <div className="hero-content">
          <h2>Plan Your Perfect Trip</h2>
          <p>Discover amazing destinations and create unforgettable memories</p>
          
          <form className="search-bar" onSubmit={handleSearch}>
            <input 
              type="text" 
              placeholder="Search for places you want to visit..."
            />
            <button type="submit">Search</button>
          </form>
        </div>
      </div>

      {/* Quick Stats Section (Optional) */}
      <div className="quick-stats">
        <div className="stat-card">
          <h3>Upcoming Trips</h3>
          <p>0</p>
        </div>
        <div className="stat-card">
          <h3>Countries Visited</h3>
          <p>0</p>
        </div>
        <div className="stat-card">
          <h3>Trips Planned</h3>
          <p>0</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
