import React, { useState, useEffect } from 'react';
import './BudgetEstimator.css';

const BudgetEstimator = ({ destination, tripDuration, travelers }) => {
  const [budgetBreakdown, setBudgetBreakdown] = useState(null);
  const [currency, setCurrency] = useState('USD');

  // Destination cost data (can be expanded with more destinations)
  const costData = {
    france: {
      currency: 'EUR',
      categories: {
        accommodation: { perNight: 120, type: 'perNight' },
        food: { perDay: 60, type: 'perDay' },
        transport: { total: 300, type: 'fixed' },
        activities: { perDay: 40, type: 'perDay' },
        miscellaneous: { total: 200, type: 'fixed' }
      }
    },
    japan: {
      currency: 'JPY',
      categories: {
        accommodation: { perNight: 15000, type: 'perNight' },
        food: { perDay: 5000, type: 'perDay' },
        transport: { total: 35000, type: 'fixed' },
        activities: { perDay: 8000, type: 'perDay' },
        miscellaneous: { total: 15000, type: 'fixed' }
      }
    },
    thailand: {
      currency: 'THB',
      categories: {
        accommodation: { perNight: 1500, type: 'perNight' },
        food: { perDay: 800, type: 'perDay' },
        transport: { total: 5000, type: 'fixed' },
        activities: { perDay: 1200, type: 'perDay' },
        miscellaneous: { total: 3000, type: 'fixed' }
      }
    },
    usa: {
      currency: 'USD',
      categories: {
        accommodation: { perNight: 150, type: 'perNight' },
        food: { perDay: 70, type: 'perDay' },
        transport: { total: 400, type: 'fixed' },
        activities: { perDay: 60, type: 'perDay' },
        miscellaneous: { total: 250, type: 'fixed' }
      }
    }
  };

  useEffect(() => {
    if (destination && tripDuration && travelers) {
      calculateBudget();
    }
  }, [destination, tripDuration, travelers]);

  const calculateBudget = () => {
    const destinationKey = destination.toLowerCase();
    const data = costData[destinationKey] || costData.france; // Default to France
    
    const breakdown = {
      currency: data.currency,
      total: 0,
      categories: []
    };

    // Calculate each category
    Object.entries(data.categories).forEach(([category, config]) => {
      let amount = 0;
      
      switch (config.type) {
        case 'perNight':
          amount = config.perNight * (tripDuration - 1) * travelers.adults;
          break;
        case 'perDay':
          amount = config.perDay * tripDuration * travelers.adults;
          break;
        case 'fixed':
          amount = config.total;
          break;
        default:
          amount = 0;
      }

      // Add children costs (50% of adult costs)
      if (travelers.children > 0) {
        switch (config.type) {
          case 'perNight':
            amount += config.perNight * 0.5 * (tripDuration - 1) * travelers.children;
            break;
          case 'perDay':
            amount += config.perDay * 0.5 * tripDuration * travelers.children;
            break;
        }
      }

      breakdown.categories.push({
        name: category.charAt(0).toUpperCase() + category.slice(1),
        amount: Math.round(amount),
        type: config.type
      });
      
      breakdown.total += amount;
    });

    breakdown.total = Math.round(breakdown.total);
    setBudgetBreakdown(breakdown);
    setCurrency(data.currency);
  };

  const getCategoryIcon = (category) => {
    const icons = {
      Accommodation: '🏨',
      Food: '🍽️',
      Transport: '✈️',
      Activities: '🎭',
      Miscellaneous: '🎁'
    };
    return icons[category] || '💰';
  };

  const getSavingsTips = () => {
    const tips = {
      france: [
        "Visit museums on first Sundays (free entry)",
        "Use weekly metro passes in Paris",
        "Eat at lunchtime for cheaper menu fixes",
        "Stay in arrondissements outside city center"
      ],
      japan: [
        "Use Japan Rail Pass for unlimited travel",
        "Stay in business hotels or capsule hotels",
        "Eat at convenience stores for quick meals",
        "Visit free temples and gardens"
      ],
      thailand: [
        "Use local buses instead of taxis",
        "Eat at street food stalls",
        "Stay in guesthouses instead of resorts",
        "Book activities through local agents"
      ],
      default: [
        "Travel during shoulder season",
        "Book flights 6-8 weeks in advance",
        "Use public transportation",
        "Cook some meals instead of eating out"
      ]
    };

    const destinationKey = destination.toLowerCase();
    return tips[destinationKey] || tips.default;
  };

  if (!budgetBreakdown) {
    return (
      <div className="budget-estimator loading">
        <div className="loading-spinner"></div>
        <p>Calculating your trip budget...</p>
      </div>
    );
  }

  return (
    <div className="budget-estimator">
      <div className="budget-header">
        <h2>💰 Estimated Trip Budget</h2>
        <p className="budget-subtitle">
          Smart budget breakdown for {travelers.adults} adult{travelers.adults > 1 ? 's' : ''}
          {travelers.children > 0 && ` & ${travelers.children} child${travelers.children > 1 ? 'ren' : ''}`}
        </p>
      </div>

      <div className="total-budget-card">
        <div className="total-amount">
          {currency} {budgetBreakdown.total.toLocaleString()}
        </div>
        <div className="total-label">Estimated Total Cost</div>
        <div className="trip-duration">
          For {tripDuration} day{tripDuration > 1 ? 's' : ''} in {destination}
        </div>
      </div>

      <div className="breakdown-section">
        <h3>Budget Breakdown</h3>
        <div className="categories-grid">
          {budgetBreakdown.categories.map((category, index) => (
            <div key={index} className="category-card">
              <div className="category-header">
                <span className="category-icon">
                  {getCategoryIcon(category.name)}
                </span>
                <span className="category-name">{category.name}</span>
              </div>
              <div className="category-amount">
                {currency} {category.amount.toLocaleString()}
              </div>
              <div className="category-bar">
                <div 
                  className="category-fill"
                  style={{
                    width: `${(category.amount / budgetBreakdown.total) * 100}%`
                  }}
                ></div>
              </div>
              <div className="category-percentage">
                {Math.round((category.amount / budgetBreakdown.total) * 100)}%
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="savings-tips">
        <h3>💡 Smart Savings Tips</h3>
        <div className="tips-grid">
          {getSavingsTips().map((tip, index) => (
            <div key={index} className="tip-card">
              <span className="tip-number">#{index + 1}</span>
              <span className="tip-text">{tip}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="budget-footer">
        <div className="disclaimer">
          <small>
            * Estimates based on average costs. Actual spending may vary based on 
            travel style, season, and personal preferences.
          </small>
        </div>
      </div>
    </div>
  );
};

export default BudgetEstimator;
