import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './BookingForm.css';

const BookingForm = () => {
    const { countryId } = useParams();
    const navigate = useNavigate();
    const { currentUser } = useAuth();
    const [country, setCountry] = useState(null);
    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);

    const [formData, setFormData] = useState({
        startDate: '',
        endDate: '',
        travelers: {
            adults: 1,
            children: 0
        },
        accommodation: 'Standard',
        specialRequests: ''
    });

    useEffect(() => {
        const fetchCountry = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/countries/${countryId}`);
                const data = await response.json();
                setCountry(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching country:', error);
                setLoading(false);
            }
        };

        fetchCountry();
    }, [countryId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'adults' || name === 'children') {
            setFormData(prev => ({
                ...prev,
                travelers: {
                    ...prev.travelers,
                    [name]: parseInt(value) || 0
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const calculateDuration = () => {
        if (formData.startDate && formData.endDate) {
            const start = new Date(formData.startDate);
            const end = new Date(formData.endDate);
            return Math.ceil((end - start) / (1000 * 60 * 60 * 24));
        }
        return 0;
    };

    const calculateTotalCost = () => {
        const duration = calculateDuration();
        const dailyCost = country?.budget?.daily || 100;
        const totalTravelers = formData.travelers.adults + formData.travelers.children;
        return dailyCost * duration * totalTravelers;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!currentUser) {
            alert('Please login to book a trip');
            navigate('/login');
            return;
        }

        setSubmitting(true);

        try {
            const bookingData = {
                userId: currentUser.userId,
                countryId: countryId,
                ...formData,
                budget: calculateTotalCost()
            };

            const response = await fetch('http://localhost:5000/api/bookings', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(bookingData)
            });

            const result = await response.json();

            if (response.ok) {
                alert(`Booking confirmed! Your confirmation number: ${result.confirmationNumber}`);
                navigate('/my-bookings');
            } else {
                alert('Booking failed: ' + result.message);
            }
        } catch (error) {
            console.error('Booking error:', error);
            alert('Booking failed. Please try again.');
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (!country) {
        return <div className="error">Country not found</div>;
    }

    const duration = calculateDuration();
    const totalCost = calculateTotalCost();

    return (
        <div className="booking-container">
            <div className="booking-header">
                <h1>Book Your Trip to {country.name}</h1>
                <p>Complete your travel details below</p>
            </div>

            <div className="booking-content">
                <div className="country-summary">
                    <img src={country.image} alt={country.name} />
                    <div className="summary-details">
                        <h3>{country.name}</h3>
                        <p>{country.description}</p>
                        <div className="country-facts">
                            <span>üèõÔ∏è {country.capital}</span>
                            <span>üíµ {country.currency}</span>
                            <span>üõ°Ô∏è {country.safetyLevel}</span>
                        </div>
                    </div>
                </div>

                <form onSubmit={handleSubmit} className="booking-form">
                    <div className="form-section">
                        <h3>Trip Dates</h3>
                        <div className="date-inputs">
                            <div className="input-group">
                                <label>Start Date</label>
                                <input
                                    type="date"
                                    name="startDate"
                                    value={formData.startDate}
                                    onChange={handleChange}
                                    min={new Date().toISOString().split('T')[0]}
                                    required
                                />
                            </div>
                            <div className="input-group">
                                <label>End Date</label>
                                <input
                                    type="date"
                                    name="endDate"
                                    value={formData.endDate}
                                    onChange={handleChange}
                                    min={formData.startDate}
                                    required
                                />
                            </div>
                        </div>
                        {duration > 0 && (
                            <p className="duration-info">Duration: {duration} days</p>
                        )}
                    </div>

                    <div className="form-section">
                        <h3>Travelers</h3>
                        <div className="traveler-inputs">
                            <div className="input-group">
                                <label>Adults</label>
                                <input
                                    type="number"
                                    name="adults"
                                    value={formData.travelers.adults}
                                    onChange={handleChange}
                                    min="1"
                                    max="10"
                                    required
                                />
                            </div>
                            <div className="input-group">
                                <label>Children</label>
                                <input
                                    type="number"
                                    name="children"
                                    value={formData.travelers.children}
                                    onChange={handleChange}
                                    min="0"
                                    max="10"
                                />
                            </div>
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>Accommodation Type</h3>
                        <select
                            name="accommodation"
                            value={formData.accommodation}
                            onChange={handleChange}
                            className="accommodation-select"
                        >
                            <option value="Budget">Budget</option>
                            <option value="Standard">Standard</option>
                            <option value="Luxury">Luxury</option>
                        </select>
                    </div>

                    <div className="form-section">
                        <h3>Special Requests</h3>
                        <textarea
                            name="specialRequests"
                            value={formData.specialRequests}
                            onChange={handleChange}
                            placeholder="Any special requirements or preferences..."
                            rows="4"
                        />
                    </div>

                    {totalCost > 0 && (
                        <div className="cost-summary">
                            <h3>Cost Summary</h3>
                            <div className="cost-details">
                                <p>Estimated Cost: <strong>${totalCost}</strong></p>
                                <p className="cost-note">* Based on average daily expenses</p>
                            </div>
                        </div>
                    )}

                    <button 
                        type="submit" 
                        disabled={submitting || !formData.startDate || !formData.endDate}
                        className="book-now-btn"
                    >
                        {submitting ? 'Booking...' : 'Confirm Booking'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default BookingForm;
