import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './Login.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: ''
  });
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      console.log('Attempting:', isLogin ? 'Login' : 'Registration', formData);
      
      const endpoint = isLogin ? '/auth/login' : '/auth/register';
      
      const response = await axios.post(`http://localhost:5000/api${endpoint}`, formData, {
        headers: {
          'Content-Type': 'application/json',
        },
        timeout: 10000
      });
      
      console.log('Response received:', response.data);
      
      if (isLogin) {
        // Login successful - FIXED based on backend response
        console.log('Login response structure:', response.data);
        
        // Adjust according to your actual backend response
        const userData = response.data.user || response.data;
        
        login({
          userId: userData.id || userData._id || userData.userId,
          username: userData.username
        }, response.data.token); // Remove token if you're not using JWT yet
        
        alert('Login successful! Redirecting...');
        window.location.href = '/dashboard';
      } else {
        // Registration successful
        alert('Registration successful! Please login with your credentials.');
        setIsLogin(true);
        // Clear form
        setFormData({ username: '', password: '', email: '' });
      }
    } catch (error) {
      console.error('Full error details:', error);
      console.error('Error response:', error.response);
      
      let errorMessage = 'An error occurred. Please try again.';
      
      if (error.response) {
        // Server responded with error status
        errorMessage = error.response.data.message || `Server error: ${error.response.status}`;
      } else if (error.request) {
        // Request was made but no response received
        errorMessage = 'Cannot connect to server. Please make sure backend is running on port 5000.';
      } else {
        // Something else happened
        errorMessage = error.message;
      }
      
      alert(`Error: ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>
          )}
          <div>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={formData.username}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          <div>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
              minLength="6"
            />
          </div>
          <button 
            type="submit" 
            disabled={loading}
            className={loading ? 'loading' : ''}
          >
            {loading ? 'Processing...' : (isLogin ? 'Login' : 'Sign Up')}
          </button>
        </form>
        
        <p className="toggle-text">
          {isLogin ? "Don't have an account? " : "Already have an account? "}
          <span 
            onClick={() => !loading && setIsLogin(!isLogin)} 
            className="toggle-link"
          >
            {isLogin ? 'Sign Up' : 'Login'}
          </span>
        </p>
        
        {/* Debug info */}
        <div className="debug-info">
          <p>Backend Status: âœ… Running on http://localhost:5000</p>
          <p>Testing: Backend is responding correctly</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
